"use strict";var body=document.querySelector("body");function tagsModal(){var aside=document.querySelector("aside"),tagsTitle=document.querySelector(".tagsTitle"),tagsBtn=document.querySelector(".tagsBtn");var counter=0;window.addEventListener("keydown",function(event){if(event.key==="Escape"&&counter){body.classList.remove("overflow-y-hidden");hideTags();};});window.addEventListener("resize",function(){if(counter&&window.innerWidth>=480){body.classList.remove("overflow-y-hidden");hideTags();};});tagsBtn.addEventListener("click",function(){if(counter){aside.classList.add("opacity-0");body.classList.remove("overflow-y-hidden");setTimeout(hideTags,300);}else{showTags();aside.classList.remove("opacity-0");};});function showTags(){body.classList.add("overflow-y-hidden");editClasses("add","remove");counter++;tagsTitle.focus();};function hideTags(){editClasses("remove","add");counter=0;tagsBtn.blur();};function editClasses(x,y){aside.classList[x]("fixed","top-0","w-screen","h-screen-minus","z-10","p-4","bg-gray-100","overflow-y-auto");tagsBtn.classList[y]("opacity-75");aside.classList[y]("hidden","mr-16");};};function imgModal(){var btnsModal=document.querySelectorAll("button.modal"),imgModal=document.querySelector("img.modal"),divModal=document.querySelector("div.modal"),exitModal=document.querySelector("div.modal button");var btnModalNow="";window.addEventListener("keydown",function(event){if(event.key==="Escape"&&!divModal.classList.contains("hidden")){hideImg();};});divModal.addEventListener("click",function(event){if(event.target.tagName!=="IMG"){hideImg();};});exitModal.addEventListener("keydown",function(event){if(event.key==="Tab"){event.preventDefault();};});function showImg(btnModal){btnModalNow=btnModal;imgModal.src=btnModal.firstElementChild.dataset.modal;body.classList.add("overflow-y-hidden");divModal.classList.remove("hidden");exitModal.focus();};function hideImg(){btnModalNow.focus();if(!imgModal.complete){transitionClasses("add");};btnModalNow.firstElementChild.classList.remove("opacity-50");divModal.classList.remove("bg-black-opacity-75");transitionClasses("add");body.classList.remove("overflow-y-hidden");setTimeout(function(){divModal.classList.add("hidden");imgModal.src="";},500);};function transitionClasses(act){divModal.firstElementChild.classList[act]("translate-y-64","opacity-0");exitModal.classList[act]("opacity-0");};btnsModal.forEach(function(btnModal){btnModal.addEventListener("click",function(){showImg(btnModal);divModal.classList.add("bg-black-opacity-75");imgModal.addEventListener("load",function(){return transitionClasses("remove");});});["mouseout","blur"].forEach(function(event){return btnModal.addEventListener(event,function(){return btnModal.firstElementChild.classList.remove("opacity-50");});});["mouseover","focus"].forEach(function(event){return btnModal.addEventListener(event,function(){return btnModal.firstElementChild.classList.add("opacity-50");});});});};tagsModal();imgModal();